package main

import (
	"crypto/rand"
	"encoding/hex"
	"testing"
	"time"

	"github.com/harmony-one/vdf/src/vdf_go"
	"github.com/stretchr/testify/assert"
)

func TestCreateProof(t *testing.T) {
	seed := []byte{0xde, 0xad, 0xbe, 0xef}

	y_buf, proof_buf := vdf_go.GenerateVDF(seed, 10, 2048)

	str_y := hex.EncodeToString(y_buf)
	str_proof := hex.EncodeToString(proof_buf)

	assert.Equal(t, "", str_y, "y match")
	assert.Equal(t, "", str_proof, "proof match")
}

func TestVerifyProof(t *testing.T) {
	seed := []byte{0xde, 0xad, 0xbe, 0xef}

	y_buf, _ := hex.DecodeString("")
	proof_buf, _ := hex.DecodeString("")

	assert.Equal(t, true, vdf_go.VerifyVDF(seed, append(y_buf, proof_buf...), 10, 2048), "must be true")
}

func TestVerifyProofSharedMemory(t *testing.T) {
	seed := []byte{0xde, 0xad, 0xbe, 0xef}

	y_buf, _ := hex.DecodeString("")
	proof_buf, _ := hex.DecodeString("")

	output := append(seed, y_buf...)
	output = append(output, proof_buf...)

	assert.Equal(t, true, vdf_go.VerifyVDF(output[:len(seed)], output[len(seed):], 10, 2048), "must be true")
}

func TestGenerateAndVerifyProof(t *testing.T) {
	seed := []byte{0xde, 0xad, 0xbe, 0xef}

	for T := 5; T < 100; T++ {
		y_buf, proof_buf := vdf_go.GenerateVDF(seed, T, 2048)
		assert.Equal(t, true, vdf_go.VerifyVDF(seed, append(y_buf, proof_buf...), T, 2048), "failed when T = %d", T)
	}
}

